import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Card, Container, Row, Col } from "react-bootstrap";

export default function SeatsPage() {
    const { flightId } = useParams();
    const [seats, setSeats] = useState([]);
    const [selectedSeat, setSelectedSeat] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8080/api/seats/flight/${flightId}`)
            .then(response => response.json())
            .then(data => setSeats(data))
            .catch(error => console.error("Ошибка загрузки мест", error));
    }, [flightId]);

    const handleSeatClick = (seat) => {
        setSelectedSeat(seat);
    };

    const groupedSeats = seats.reduce((acc, seat) => {
        acc[seat.rowNumber] = acc[seat.rowNumber] || [];
        acc[seat.rowNumber].push(seat);
        return acc;
    }, {});

    const getSeatLayout = (rowSeats) => {
        const seatCount = rowSeats.length;
        let layout = [];
        let partitions = [];

        if (seatCount === 2) partitions = [1];
        else if (seatCount === 4) partitions = [2];
        else if (seatCount === 6) partitions = [2, 2];
        else if (seatCount === 8) partitions = [2, 3];
        else if (seatCount === 10) partitions = [3, 4];

        let index = 0;
        partitions.forEach((size, idx) => {
            layout.push(rowSeats.slice(index, index + size));
            layout.push(null); // Добавляем проход
            index += size;
        });
        layout.pop(); // Убираем последний лишний проход

        return layout;
    };

    return (
        <Container className="mt-4">
            <h1 className="text-center mb-4">План салона самолета</h1>
            <Row>
                <Col md={8}>
                    {Object.keys(groupedSeats).map(row => (
                        <div key={row} className="d-flex mb-2">
                            {getSeatLayout(groupedSeats[row]).map((seat, index) => (
                                seat ? (
                                    <div 
                                        key={seat.id} 
                                        className={`seat ${seat.isOccupied ? "bg-danger" : "bg-success"}`} 
                                        onClick={() => handleSeatClick(seat)}
                                        style={{
                                            width: "50px", 
                                            height: "50px", 
                                            margin: "5px", 
                                            display: "flex", 
                                            alignItems: "center", 
                                            justifyContent: "center", 
                                            color: "white", 
                                            cursor: "pointer",
                                            borderRadius: "5px"
                                        }}
                                    >
                                        {seat.rowNumber}{String.fromCharCode(65 + seat.seatIndex)}
                                    </div>
                                ) : (
                                    <div 
                                        key={`aisle-${row}-${index}`} 
                                        style={{
                                            width: "50px", 
                                            height: "50px", 
                                            margin: "5px", 
                                            visibility: "hidden"
                                        }}
                                    />
                                )
                            ))}
                        </div>
                    ))}
                </Col>
                <Col md={4}>
                    {selectedSeat && (
                        <Card>
                            <Card.Body>
                                <h5>Детали места</h5>
                                <p><strong>Ряд:</strong> {selectedSeat.rowNumber}</p>
                                <p><strong>Место:</strong> {selectedSeat.rowNumber}{String.fromCharCode(65 + selectedSeat.seatIndex)}</p>
                                <p><strong>Класс:</strong> {selectedSeat.seatClass}</p>
                                <p><strong>Цена:</strong> {selectedSeat.price.toFixed(2)} €</p>
                                <p><strong>Статус:</strong> {selectedSeat.isOccupied ? "Занято" : "Свободно"}</p>
                            </Card.Body>
                        </Card>
                    )}
                </Col>
            </Row>
        </Container>
    );
}
