import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Card } from "react-bootstrap";

export default function SeatsPage() {
    const { flightId } = useParams();
    const [seats, setSeats] = useState([]);
    const [selectedSeat, setSelectedSeat] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8080/api/seats/flight/${flightId}`)
            .then(response => response.json())
            .then(data => setSeats(data))
            .catch(error => console.error("Ошибка загрузки мест", error));
    }, [flightId]);

    const handleSeatClick = (seat) => {
        setSelectedSeat(seat);
    };


    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">План мест в самолете</h1>
            <div className="seat-map">
                {seats.reduce((acc, seat) => {
                    if (!acc[seat.rowNumber]) {
                        acc[seat.rowNumber] = [];
                    }
                    acc[seat.rowNumber].push(seat);
                    return acc;
                }, {}).map((row, index) => (
                    <div key={index} className="seat-row">
                        {row.map(seat => (
                            <div 
                                key={seat.id} 
                                className={`seat ${seat.isOccupied ? "occupied" : "available"}`} 
                                onClick={() => handleSeatClick(seat)}
                            >
                                {seat.seatIndex}
                            </div>
                        ))}
                    </div>
                ))}
            </div>

            {selectedSeat && (
                <div className="seat-details">
                    <h3>Детали места</h3>
                    <p><strong>Ряд:</strong> {selectedSeat.rowNumber}</p>
                    <p><strong>Место:</strong> {selectedSeat.seatIndex}</p>
                    <p><strong>Класс:</strong> {selectedSeat.seatClass}</p>
                    <p><strong>Цена:</strong> {selectedSeat.price.toFixed(2)} €</p>
                    <p><strong>Статус:</strong> {selectedSeat.isOccupied ? "Занято" : "Свободно"}</p>
                </div>
            )}
        </div>
    );
}
